import{_ as e,o as a,c as i,a as n}from"./app.b90595f9.js";const u=JSON.parse('{"title":"css动画","description":"","frontmatter":{},"headers":[{"level":2,"title":"Css Animation 属性笔记","slug":"css-animation-属性笔记","link":"#css-animation-属性笔记","children":[]}],"relativePath":"css/css-animation.md","lastUpdated":1716447218000}'),t={name:"css/css-animation.md"},s=n('<h1 id="css动画" tabindex="-1">css动画 <a class="header-anchor" href="#css动画" aria-hidden="true">#</a></h1><p><a href="https://www.zhangxinxu.com/wordpress/2010/11/css3-transitions-transforms-animation-introduction/" target="_blank" rel="noreferrer">CSS3 Transitions, Transforms和Animation使用简介与应用展示</a></p><p><a href="https://www.zhangxinxu.com/wordpress/2018/06/css3-animation-steps-step-start-end/" target="_blank" rel="noreferrer">CSS3 animation属性中的steps功能符深入介绍</a></p><h2 id="css-animation-属性笔记" tabindex="-1">Css Animation 属性笔记 <a class="header-anchor" href="#css-animation-属性笔记" aria-hidden="true">#</a></h2><ul><li><code>animation-name</code> 需要绑定到选择器的 keyframe 名称</li><li><code>animation-duration</code> 完成动画所花费的时间，以秒或毫秒计</li><li><code>animation-timing-function</code> 规定动画的速度曲线</li><li><code>animation-delay</code> 在动画开始之前的延迟</li><li><code>animation-iteration-count</code> 动画应该播放的次数</li><li><code>animation-direction</code> 是否应该轮流反向播放动画 <ul><li><code>normal</code> – 动画在每个周期 向前 播放。换句话说，每次动画循环时，动画都会重置为开始状态并重新开始。这是默认值。</li><li><code>reverse</code> – 动画在每个周期 向后 播放。换句话说，每次动画循环时，动画都会重置为结束状态并重新开始。动画步骤是反向执行的，计时功能也是反向执行的。例如， ease-in 定时函数变为 ease-out。</li><li><code>alternate</code> – 动画在每个周期中反转方向，第一次迭代 向前播放。确定周期是偶数还是奇数的计数从 1 开始。</li><li><code>alternate-reverse</code> – 动画在每个周期中反转方向，第一次迭代向 后播放。确定周期是偶数还是奇数的计数从 1 开始。</li></ul></li></ul>',5),o=[s];function r(c,d,l,m,_,h){return a(),i("div",null,o)}const f=e(t,[["render",r]]);export{u as __pageData,f as default};
